import { Icon } from "element-ui";
import Vue from "vue";
import VueRouter from "vue-router";

Vue.use(VueRouter);
const routes = [
    //显示首页
    {
      path: "/",
      redirect: "/login",
      name: "显示首页",
      icon:'null',
      num:0,
      type:'C',
    },
    {
      //一级路由
      path: "/login",
      name: "登录",
      num:1,
      type:'C',
      icon:'el-icon-s-tools',
      //定义图标
      component: () => import("@/views/login"),
    },
    {
      //一级路由
      path: "/home",
      redirect:"/home/users",
      name: "系统管理",
      icon:'el-icon-user-solid',
      num:2,
      type:'C',
      meta: { title: '系统管理'},
      hidden: false,
      //定义图标
      component: () => import("@/layout/home"),
      children: [
        {
          path: "/home/users",
          name:"用户管理",
          num:21,
          icon:'el-icon-s-order',
          type:'C',
          meta: {
            title: '用户管理',
          },
          component: () => import("@/views/users"),
        },
        {
          path: "/home/Menu",
          name:"菜单管理",
          num:22,
          icon:'el-icon-s-operation',
          type:'C',
          meta: {
            title: '菜单管理',
          },
          component: () => import("@/views/Menu"),
        },
      ],
    },
    {
      //一级路由
      path: "/booksInfoMP",
      name: "图书信息",
      //定义图标
      component: () => import("@/views/listPage"),
    },
    {
      //一级路由
      path: "/home",
      name: "数据管理",
      num:3,
      icon:'el-icon-s-order',
      type:'C',
      meta: {
        title: '数据管理',
      },
      //定义图标
      component: () => import("@/layout/home"),
    },
   
    {
      //一级路由
      path: "/home",
      name: "系统工具",
      num:5,
      icon:'el-icon-s-tools',
      type:'C',
      meta: {
        title: '系统工具',
      },
      //定义图标
      component: () => import("@/layout/home"),
    }
  ];
  
  
  const originalPush = VueRouter.prototype.push;
  VueRouter.prototype.push = function push(location) {
    return originalPush.call(this, location).catch((err) => err);
  };
  
  const router = new VueRouter({
    routes,
    mode: "history",
  });
  
  export default router;
  export { routes };
