import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.iterator.reduce.js";
import axios from 'axios';
export default {
  name: 'BreadOrderManagement',
  data() {
    return {
      // 订单数据
      orders: [],
      filteredOrders: [],
      selectedOrder: null,
      // 筛选和排序
      searchQuery: '',
      statusFilter: '',
      dateRange: {
        start: '',
        end: ''
      },
      sortKey: 'orderDate',
      sortOrder: 'desc',
      // 分页
      currentPage: 1,
      pageSize: 10,
      // 模态框状态
      showAddOrderModal: false,
      showEditOrderModal: false,
      showDeleteConfirmation: false,
      orderToDelete: null,
      // 订单表单
      orderForm: {
        customerName: '',
        phone: '',
        address: '',
        pickupTime: '',
        paymentMethod: 'cash',
        status: 'pending',
        notes: '',
        items: [],
        discount: 0,
        deliveryFee: 0
      }
    };
  },
  computed: {
    // 总订单数
    totalOrders() {
      return this.orders.length;
    },
    // 待处理订单数
    pendingOrders() {
      return this.orders.filter(order => order.status === 'pending').length;
    },
    // 处理中订单数
    processingOrders() {
      return this.orders.filter(order => order.status === 'processing').length;
    },
    // 已完成订单数
    completedOrders() {
      return this.orders.filter(order => order.status === 'completed').length;
    },
    // 已取消订单数
    cancelledOrders() {
      return this.orders.filter(order => order.status === 'cancelled').length;
    },
    // 总收入
    totalRevenue() {
      return this.orders.filter(order => order.status === 'completed').reduce((total, order) => total + parseFloat(order.totalAmount), 0);
    },
    // 总页数
    totalPages() {
      return Math.ceil(this.filteredOrders.length / this.pageSize);
    },
    // 当前页订单
    paginatedOrders() {
      const startIndex = (this.currentPage - 1) * this.pageSize;
      const endIndex = startIndex + this.pageSize;
      return this.filteredOrders.slice(startIndex, endIndex);
    },
    // 显示的页码
    displayedPages() {
      const pages = [];
      const maxDisplayedPages = 5;
      if (this.totalPages <= maxDisplayedPages) {
        // 总页数小于等于最大显示页数，显示所有页码
        for (let i = 1; i <= this.totalPages; i++) {
          pages.push(i);
        }
      } else {
        // 总页数大于最大显示页数
        const halfDisplay = Math.floor(maxDisplayedPages / 2);

        // 当前页靠近开始
        if (this.currentPage <= halfDisplay + 1) {
          for (let i = 1; i <= maxDisplayedPages; i++) {
            pages.push(i);
          }
        }
        // 当前页靠近结束
        else if (this.currentPage >= this.totalPages - halfDisplay) {
          for (let i = this.totalPages - maxDisplayedPages + 1; i <= this.totalPages; i++) {
            pages.push(i);
          }
        }
        // 当前页在中间
        else {
          for (let i = this.currentPage - halfDisplay; i <= this.currentPage + halfDisplay; i++) {
            pages.push(i);
          }
        }
      }
      return pages;
    }
  },
  created() {
    this.loadOrders();
  },
  methods: {
    // 加载订单数据
    async loadOrders() {
      try {
        // 从后端API获取数据
        const response = await axios.get('http://localhost:5050/api/orders');
        console.log('获取到订单数据:', response.data.length);

        // 处理日期字符串为Date对象，并确保数值字段为数字类型
        this.orders = response.data.map(order => ({
          ...order,
          orderDate: new Date(order.orderDate),
          pickupTime: order.pickupTime ? new Date(order.pickupTime) : null,
          totalAmount: parseFloat(order.totalAmount),
          discount: parseFloat(order.discount || 0),
          deliveryFee: parseFloat(order.deliveryFee || 0),
          items: order.items.map(item => ({
            ...item,
            price: parseFloat(item.price),
            quantity: parseInt(item.quantity)
          }))
        }));
        this.filterOrders();
      } catch (error) {
        console.error('加载订单数据失败:', error);
        this.orders = [];
        this.filterOrders();
      }
    },
    // 刷新订单数据
    async refreshOrders() {
      try {
        await this.loadOrders();
        this.selectedOrder = null;
        this.$nextTick(() => {
          alert('订单数据已刷新');
        });
      } catch (error) {
        console.error('刷新订单数据失败:', error);
        alert('刷新订单数据失败，请重试');
      }
    },
    // 筛选订单
    filterOrders() {
      let result = [...this.orders];

      // 搜索条件筛选
      if (this.searchQuery) {
        const query = this.searchQuery.toLowerCase();
        result = result.filter(order => order.orderNumber && order.orderNumber.toLowerCase().includes(query) || order.customerName && order.customerName.toLowerCase().includes(query) || order.phone && order.phone.includes(query));
      }

      // 状态筛选
      if (this.statusFilter) {
        result = result.filter(order => order.status === this.statusFilter);
      }

      // 日期范围筛选
      if (this.dateRange.start) {
        const startDate = new Date(this.dateRange.start);
        startDate.setHours(0, 0, 0, 0);
        result = result.filter(order => {
          const orderDate = new Date(order.orderDate);
          return orderDate >= startDate;
        });
      }
      if (this.dateRange.end) {
        const endDate = new Date(this.dateRange.end);
        endDate.setHours(23, 59, 59, 999);
        result = result.filter(order => {
          const orderDate = new Date(order.orderDate);
          return orderDate <= endDate;
        });
      }
      this.filteredOrders = this.sortOrders(result);
      this.currentPage = 1; // 重置到第一页
    },
    // 重置筛选条件
    resetFilters() {
      this.searchQuery = '';
      this.statusFilter = '';
      this.dateRange.start = '';
      this.dateRange.end = '';
      this.filterOrders();
    },
    // 排序订单
    sortOrders(ordersToSort = null) {
      const orders = ordersToSort || [...this.filteredOrders];
      return orders.sort((a, b) => {
        let valueA = a[this.sortKey];
        let valueB = b[this.sortKey];

        // 日期类型特殊处理
        if (this.sortKey === 'orderDate' || this.sortKey === 'pickupTime') {
          valueA = new Date(valueA).getTime();
          valueB = new Date(valueB).getTime();
        }

        // 字符串类型特殊处理
        if (typeof valueA === 'string') {
          valueA = valueA.toLowerCase();
          valueB = valueB.toLowerCase();
        }

        // 根据排序方向返回比较结果
        if (this.sortOrder === 'asc') {
          return valueA > valueB ? 1 : -1;
        } else {
          return valueA < valueB ? 1 : -1;
        }
      });
    },
    // 切换排序方向
    toggleSortOrder() {
      this.sortOrder = this.sortOrder === 'asc' ? 'desc' : 'asc';
      this.filteredOrders = this.sortOrders();
    },
    // 更改页码
    changePage(page) {
      if (page >= 1 && page <= this.totalPages) {
        this.currentPage = page;
      }
    },
    // 查看订单详情
    viewOrderDetails(order) {
      this.selectedOrder = order;
    },
    // 关闭订单详情
    closeOrderDetails() {
      this.selectedOrder = null;
    },
    // 编辑订单
    editOrder(order) {
      // 复制订单数据到表单
      this.orderForm = {
        id: order.id,
        orderNumber: order.orderNumber,
        customerName: order.customerName,
        phone: order.phone,
        address: order.address || '',
        pickupTime: this.formatDateTimeForInput(order.pickupTime),
        paymentMethod: order.paymentMethod,
        status: order.status,
        notes: order.notes || '',
        items: JSON.parse(JSON.stringify(order.items)),
        // 深拷贝
        discount: order.discount || 0,
        deliveryFee: order.deliveryFee || 0
      };
      this.showEditOrderModal = true;
    },
    // 确认删除订单
    confirmDeleteOrder(order) {
      this.orderToDelete = order;
      this.showDeleteConfirmation = true;
    },
    // 取消删除
    cancelDelete() {
      this.orderToDelete = null;
      this.showDeleteConfirmation = false;
    },
    // 删除订单
    async deleteOrder() {
      try {
        await axios.delete(`http://localhost:5050/api/orders/${this.orderToDelete.id}`);

        // 重新加载订单数据
        await this.loadOrders();

        // 如果删除的是当前选中的订单，则清除选中
        if (this.selectedOrder && this.selectedOrder.id === this.orderToDelete.id) {
          this.selectedOrder = null;
        }
        this.showDeleteConfirmation = false;
        this.orderToDelete = null;
        alert('订单删除成功');
      } catch (error) {
        console.error('删除订单失败:', error);
        alert('删除订单失败，请重试');
      }
    },
    // 更新订单状态
    async updateOrderStatus(orderId, newStatus) {
      try {
        await axios.put(`http://localhost:5050/api/orders/${orderId}/status`, {
          status: newStatus
        });

        // 重新加载订单数据
        await this.loadOrders();

        // 如果当前有选中的订单，也更新它的状态
        if (this.selectedOrder && this.selectedOrder.id === orderId) {
          this.selectedOrder.status = newStatus;
        }
      } catch (error) {
        console.error('更新订单状态失败:', error);
        alert('更新订单状态失败，请重试');
      }
    },
    // 打印订单
    printOrder(order) {
      // 在实际应用中，这里应该调用打印功能
      console.log('打印订单:', order.orderNumber);
      alert(`正在打印订单 #${order.orderNumber}`);
    },
    // 添加面包商品
    addBreadItem() {
      this.orderForm.items.push({
        name: '',
        breadType: 'sourdough',
        price: 0,
        quantity: 1
      });
    },
    // 移除面包商品
    removeBreadItem(index) {
      this.orderForm.items.splice(index, 1);
    },
    // 关闭订单模态框
    closeOrderModal() {
      this.showAddOrderModal = false;
      this.showEditOrderModal = false;
      this.resetOrderForm();
    },
    // 重置订单表单
    resetOrderForm() {
      this.orderForm = {
        customerName: '',
        phone: '',
        address: '',
        pickupTime: '',
        paymentMethod: 'cash',
        status: 'pending',
        notes: '',
        items: [],
        discount: 0,
        deliveryFee: 0
      };

      // 添加一个默认的空商品项
      this.addBreadItem();
    },
    // 保存订单
    async saveOrder() {
      try {
        const totalAmount = this.calculateOrderTotal();

        // 确保数据类型正确
        const orderData = {
          ...this.orderForm,
          totalAmount: parseFloat(totalAmount.toFixed(2)),
          discount: parseFloat(this.orderForm.discount || 0),
          deliveryFee: parseFloat(this.orderForm.deliveryFee || 0),
          items: this.orderForm.items.map(item => ({
            ...item,
            price: parseFloat(item.price),
            quantity: parseInt(item.quantity)
          }))
        };
        if (this.showEditOrderModal) {
          // 更新现有订单
          await axios.put(`http://localhost:5050/api/orders/${this.orderForm.id}`, orderData);
          console.log('订单更新成功:', orderData);
        } else {
          // 创建新订单
          await axios.post('http://localhost:5050/api/orders', orderData);
          console.log('订单创建成功:', orderData);
        }

        // 重新加载订单数据
        await this.loadOrders();
        this.closeOrderModal();
        alert(this.showEditOrderModal ? '订单更新成功' : '订单创建成功');
      } catch (error) {
        console.error('保存订单失败:', error);
        alert('保存订单失败，请重试');
      }
    },
    // 计算订单小计
    calculateSubtotal(items) {
      return items.reduce((total, item) => total + parseFloat(item.price) * parseInt(item.quantity), 0);
    },
    // 计算订单总计
    calculateOrderTotal() {
      const subtotal = this.calculateSubtotal(this.orderForm.items);
      const discount = parseFloat(this.orderForm.discount) || 0;
      const deliveryFee = parseFloat(this.orderForm.deliveryFee) || 0;
      return subtotal - discount + deliveryFee;
    },
    // 获取订单状态文本
    getStatusText(status) {
      const statusMap = {
        'pending': '待处理',
        'processing': '处理中',
        'completed': '已完成',
        'cancelled': '已取消'
      };
      return statusMap[status] || status;
    },
    // 获取支付方式文本
    getPaymentMethodText(method) {
      const methodMap = {
        'cash': '现金支付',
        'wechat': '微信支付',
        'alipay': '支付宝',
        'card': '银行卡'
      };
      return methodMap[method] || method;
    },
    // 获取面包类型文本
    getBreadTypeText(type) {
      const typeMap = {
        'sourdough': '酸面团面包',
        'baguette': '法棍面包',
        'croissant': '牛角面包',
        'wholewheat': '全麦面包',
        'brioche': '布里欧面包',
        'rye': '黑麦面包',
        'ciabatta': '夏巴塔面包',
        'bagel': '贝果面包',
        'focaccia': '佛卡夏面包',
        'cake': '蛋糕',
        'other': '其他'
      };
      return typeMap[type] || type;
    },
    // 获取面包图片
    getBreadImage(type) {
      // 在实际应用中，这里应该返回不同面包类型的图片URL
      // 这里使用一个简单的占位符图片
      return `data:image/svg+xml;base64,`;
    },
    // 获取下一个状态
    getNextStatus(status) {
      const statusFlow = {
        'pending': 'processing',
        'processing': 'completed',
        'completed': 'completed',
        'cancelled': 'pending'
      };
      return statusFlow[status] || status;
    },
    // 获取状态操作文本
    getStatusActionText(status) {
      const actionMap = {
        'pending': '开始处理',
        'processing': '标记完成',
        'completed': '已完成',
        'cancelled': '重新处理'
      };
      return actionMap[status] || '更新状态';
    },
    // 格式化日期
    formatDate(date) {
      if (!date) return '';
      const d = new Date(date);
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')}`;
    },
    // 格式化时间
    formatTime(date) {
      if (!date) return '';
      const d = new Date(date);
      return `${String(d.getHours()).padStart(2, '0')}:${String(d.getMinutes()).padStart(2, '0')}`;
    },
    // 格式化日期时间
    formatDateTime(date) {
      if (!date) return '';
      return `${this.formatDate(date)} ${this.formatTime(date)}`;
    },
    // 格式化日期时间为输入框格式
    formatDateTimeForInput(date) {
      if (!date) return '';
      const d = new Date(date);
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')}T${String(d.getHours()).padStart(2, '0')}:${String(d.getMinutes()).padStart(2, '0')}`;
    }
  }
};
